INSERT INTO `xxshop_article` VALUES ('32776', '2012-12-23 21:44:01', '', '<p style=\"text-indent:24.0pt;\">\r\n	<span></span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;color:black;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">北京中华易达科技有限公司是一家高新科技创业公司，由多位</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">web</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">开发行业内知名专家、国内著名开源框架</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">EasyJWeb</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">创始人、</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">CSDN</span></span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\"> Java</span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;color:black;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">论坛版主、电子工业出版社</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">Java</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">专家专业作者共同成立于</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">2010</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">年，是一家提供专业软件服务、网络服务和企业级解决方案的</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">IT</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">服务提供商。公司立足自我创业，专门从事</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">web</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">开发及系统集成业务服务，公司秉承人才之上、诚信经营的策略，公司的核心业务为面向国内外客户提供全方位、高品质的信息系统服务、综合业务系统咨询服务和集成技术服务。</span><span></span></span> \r\n</p>\r\n<p style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16.0pt;font-family:仿宋_GB2312;color:black;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">中华易达将先进的软件技术与</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">IT</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">服务相结合，为政府、企业客户提供高效的信息化解决方案和构筑服务；并通过优秀的咨询服务及强大的</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">IT</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">服务体系，支持并保障企事业客户业务的正常、高效运行。我们为客户提供的不仅仅是单纯个别的</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">IT</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">服务，而是针对客户要求，提供包括</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">IT</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">系统咨询、构筑、维护、运营在内的综合、全面的一站式</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">IT</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">服务。</span></span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span> \r\n</p>\r\n<p>\r\n	<b><span style=\"font-size:16.0pt;font-family:楷体_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">关于我们：</span><span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16.0pt;font-family:Arial;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp; </span></span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">求知是艰苦的，但我们有足够的劲头；</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16.0pt;font-family:Arial;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp; </span></span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">创业是艰辛的，但我们有足够的激情；</span><span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16.0pt;font-family:Arial;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp; </span></span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">成功是艰难的，但我们有正确的方向；</span><span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16.0pt;font-family:Arial;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp; </span></span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">经过时间的洗礼，我们惟有不变的是年轻的心、旺盛的激情，年轻的我们不图回报，有的只是感恩。</span><span style=\"font-size:16.0pt;font-family:Arial;\"></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">我们的文化</span></b><span style=\"font-size:16.0pt;font-family:Arial;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">　　诚信</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">:</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">诚信是我们的安身立命之本，忠诚于企业和工作职责。</span></span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">　　敬业</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">:</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">不断挑战困难、挑战自我、不断的提高、完善，追求做到最好。</span></span><span style=\"font-size:16.0pt;font-family:Arial;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">　　创新</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">:</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">不断超越过去，不断进取，善于学习、总结和提炼。</span></span><span style=\"font-size:16.0pt;font-family:Arial;\"></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"font-size:16.0pt;font-family:楷体_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">我们的宗旨</span><span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:SimSun;color:#999999;\">　　</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">以人为本</span><span style=\"font-size:16.0pt;font-family:Arial;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:SimSun;color:#999999;\">　　</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">以客户为中心</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:SimSun;color:#999999;\">　　</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">以知识为动力</span><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">&nbsp;</span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"font-size:16.0pt;font-family:楷体_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;color:#999999;\">我们的目标</span><span></span></span></b> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;font-family:SimSun;\"><span></span><span style=\"color:#999999;\">开发每一款让用户满意的软件产品</span></span><span></span></span><span style=\"font-size:16.0pt;font-family:仿宋_GB2312;\"><span style=\